mapper.select:
SELECT COUNT(USER_ID) AS CNT
FROM   t_wlk_MNG_USER
WHERE  USER_ID = #{userId}

SELECT COUNT(USER_ID) AS CNT
FROM   t_wlk_MNG_USER
WHERE  USERGROUP_ID = #{userGroupId}

mapper.update:
UPDATE t_wlk_MNG_USER SET USER_NM = #{userNm},
USER_PW = #{userPw}, PW_MODIFY_DT = NOW(),
EMAIL_ADDR = #{emailAddr}, PHONE_NO = #{phoneNo, jdbcType=VARCHAR}, DEPT_NM = #{deptNm}, USER_DESC = #{userDesc, jdbcType=VARCHAR}, MODIFY_ID = #{modifyId}, MODIFY_DT = NOW() WHERE USER_ID = #{userId}
