mapper.select:
SELECT SQL_CALC_FOUND_ROWS my.USER_ID, MAX(my.REGIST_DT) AS REGIST_DT, MAX(my.USER_NM) AS USER_NM, MAX(my.MOBILE) AS MOBILE, MAX(my.SX) AS SX, MAX(my.EMAIL_ID) AS EMAIL_ID, MAX(my.BRTH_DT) AS BRTH_DT, MAX(my.GRP_TP) AS GRP_TP, MAX(req.REQ_TP) AS REQ_TP, MAX(alt.ALT_TP) AS ALT_TP, ( SELECT ic.USER_NM FROM t_walk.T_WLK_MNG_USER ic WHERE ic.USER_ID = my.IN_CHARGE_ID ) AS IN_CHARGE_NM, my.IN_CHARGE_ID, req.REQ_ID FROM t_walk.t_wlk_my my LEFT OUTER JOIN t_walk.t_wlk_mng_req req ON req.USER_ID = my.USER_ID LEFT OUTER JOIN t_walk.t_wlk_mng_alt alt ON req.ALRM_ID = alt.ALRM_ID INNER JOIN t_walk.t_wlk_mng_user admin ON admin.USER_ID = my.IN_CHARGE_ID WHERE 1=1
-- 담당자에 맞는 조회 -- 상위 담당자인 경우, 하위 담당자들의 USER_ID를 조회 시에 전부 넣어주고, -- 하위 담당자(고객 바로 상위)의 경우, 본인의 USER_ID를 넣어서 조회한다. AND my.IN_CHARGE_ID IN ${inChargeIds}
AND my.REGIST_DT >= #{searchBgnDe}
AND my.REGIST_DT <= #{searchEndDe}
AND req.REQ_TP IS NOT NULL AND req.REQ_TP != ''
AND (req.REQ_TP IS NULL OR req.REQ_TP = '')
AND alt.ALT_TP IS NOT NULL AND alt.ALT_TP != ''
AND (alt.ALT_TP IS NULL OR alt.ALT_TP = '')
AND my.USER_NM = #{userNm}
AND my.EMAIL_ID LIKE CONCAT('%',#{emailId},'%')
AND my.USER_ID LIKE CONCAT('%',#{userId},'%')
AND my.MOBILE = #{mobile}
AND my.SX = #{sx}
AND my.BRTH_DT = #{brthDt}
AND admin.USER_NM = #{inChargeNm}
AND my.GRP_TP = #{grpTp}
GROUP BY my.USER_ID ORDER BY
my.REGIST_DT
req.REQ_TP
my.USER_ID
my.SX
my.GRP_TP
my.REGIST_DT
DESC
ASC
LIMIT #{rowNo}, #{rows}

SELECT SQL_CALC_FOUND_ROWS alt.TRK_ID, alt.USER_ID, alt.DCT_DT, alt.ALT_TP, alt.ALT_RMRK, my.GRP_TP, admin.USER_NM AS IN_CHARGE_NM, alt.ALT_STT FROM t_walk.t_wlk_mng_alt alt INNER JOIN t_walk.t_wlk_my my ON alt.USER_ID = my.USER_ID LEFT OUTER JOIN ( SELECT USER_ID, USER_NM FROM t_walk.t_wlk_mng_user ) admin ON my.IN_CHARGE_ID = admin.USER_ID WHERE 1=1
AND my.USER_ID = #{userId}
AND alt.DCT_DT >= #{searchBgnDe}
AND alt.DCT_DT <= #{searchEndDe}
AND alt.ALT_TP IN (${altTp})
ORDER BY alt.DCT_DT DESC LIMIT #{rowNo}, #{rows}

mapper.update:
UPDATE
	t_walk.t_wlk_mng_alt
SET
	ALT_STT = #{altStt}
WHERE
	TRK_ID = #{trkId}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS req.REQ_ID, req.USER_ID, req.REQ_DT, req.REQ_TP, my.GRP_TP, admin.USER_NM AS IN_CHARGE_NM, req.REQ_STT FROM t_walk.t_wlk_mng_req req INNER JOIN t_walk.t_wlk_my my ON req.USER_ID = my.USER_ID LEFT OUTER JOIN ( SELECT USER_ID, USER_NM FROM t_walk.t_wlk_mng_user ) admin ON my.IN_CHARGE_ID = admin.USER_ID WHERE 1 = 1
AND req.USER_ID = #{userId}
AND req.REQ_DT >= #{searchBgnDe}
AND req.REQ_DT <= #{searchEndDe}
AND req.REQ_TP IN (${reqTp})
AND req.REQ_STT = #{reqStt}
AND admin.USER_ID = #{inChargeId}
AND admin.USER_ID IN (${inChargeIds})
ORDER BY req.REQ_DT DESC LIMIT #{rowNo}, #{rows}

mapper.update:
UPDATE
	t_walk.t_wlk_mng_req
SET
	REQ_STT = #{reqStt}
WHERE
	REQ_ID = #{reqId}

mapper.select:
SELECT COUNT(USER_ID) AS CNT
FROM   t_wlk_MNG_USER
WHERE  USER_ID = #{userId}

SELECT COUNT(USER_ID) AS CNT
FROM   t_wlk_MNG_USER
WHERE  USERGROUP_ID = #{userGroupId}

mapper.update:
UPDATE t_wlk_MNG_USER SET USER_NM = #{userNm},
USER_PW = #{userPw}, PW_MODIFY_DT = NOW(),
EMAIL_ADDR = #{emailAddr}, PHONE_NO = #{phoneNo, jdbcType=VARCHAR}, DEPT_NM = #{deptNm}, USER_DESC = #{userDesc, jdbcType=VARCHAR}, MODIFY_ID = #{modifyId}, MODIFY_DT = NOW() WHERE USER_ID = #{userId}

        UPDATE t_wlk_my
        SET SX          = #{gender}
	      , BRTH_DT     = #{brthDt}
	      , BIRTH_YEAR  = #{brthYear}
          , UPT_DT      = now()
          , UPT_ID      = #{adminId}
		WHERE USER_ID   = #{userId}
