mapper.select:
select SQL_CALC_FOUND_ROWS
    WRKFRC.*, GRP.GRP_NM
from T_WLK_MNG_WRKFRC WRKFRC
	LEFT OUTER JOIN T_WLK_MNG_GRP GRP ON GRP.GRP_ID = WRKFRC.GRP_ID
WHERE 1=1

	AND WRKFRC.WRKFRC_NMBR LIKE CONCAT('%',#{wrkfrcNmbr},'%')


	AND WRKFRC.WRKFRC_NM = #{wrkfrcNm}


	AND WRKFRC.WRKFRC_MOBILE = #{wrkfrcMobile}


	AND WRKFRC.WRKFRC_BRTH_DT = #{wrkfrcBrthDt}


	AND WRKFRC.SVC_TP IN (${svcTp})


	AND WRKFRC.GRP_ID = #{grpId}


	AND GRP.GRP_NM = #{grpNm}


	AND WRKFRC.WRKFRC_SX = #{wrkfrcSx}

ORDER BY


		WRKFRC.WRKFRC_NMBR


		WRKFRC.WRKFRC_SX


		grp.GRP_NM


		WRKFRC.SVC_TP


		WRKFRC.REGIST_DT




		DESC


		ASC


LIMIT #{rowNo}, #{rows}
